        static bool SurviveScenario(int successRate, List<Item> inventory)
        {
            // Adjust the base success rate based on the effects of items
            int adjustedSuccessRate = successRate;

            foreach (var item in inventory)
            {
                if (item is Flashlight)
                {
                    // If the player has a flashlight, reduce the difficulty
                    adjustedSuccessRate += 10;
                }
                else if (item is Boat)
                {
                    // If the player has a boat, reduce the difficulty
                    adjustedSuccessRate += 8;
                }
                else if (item is ClimbingGear)
                {
                    // If the player has climbing gear, reduce the difficulty
                    adjustedSuccessRate += 5;
                }

                // Add more conditions for other items and their effects
            }

            Random random = new Random();
            int outcome = random.Next(1, 101); // Generate a random number between 1 and 100

            return outcome <= adjustedSuccessRate;
        }

        // Function to simulate the chance of finding a first aid kit and replenishing lives
        static bool TryReplenishLives()
        {
            Random random = new Random();
            int outcome = random.Next(1, 101); // Generate a random number between 1 and 100

            return outcome <= 100; // 100% chance of repleneshing life
        }

