    static void DeleteSavedGame()
    {
        Console.Clear();
        Console.WriteLine("Delete Saved Game:");

        string[] savedGames = Directory.GetFiles(SaveDirectory);

        if (savedGames.Length == 0)
        {
            Console.WriteLine("No saved games found.");
            Console.WriteLine("Press 'enter' to continue...");
            Console.ReadLine();
            return;
        }

        Console.WriteLine("Select the saved game to delete:");
        for (int i = 0; i < savedGames.Length; i++)
        {
            Console.WriteLine($"{i + 1}. {Path.GetFileName(savedGames[i])}");
        }

        Console.Write("Enter the number of the saved game to delete: ");
        if (int.TryParse(Console.ReadLine(), out int selectedGame) && selectedGame > 0 && selectedGame <= savedGames.Length)
        {
            string selectedGamePath = savedGames[selectedGame - 1];
            File.Delete(selectedGamePath);
            Console.WriteLine($"Saved game '{Path.GetFileName(selectedGamePath)}' deleted successfully!");
        }
        else
        {
            Console.WriteLine("Invalid input. Press 'enter' to continue...");
            Console.ReadLine();
        }
    }
   
    static void SaveGameState(GameState gameState)
    {
        try
        {
            using (FileStream fileStream = new FileStream("savegame.dat", FileMode.Create))
            {
                var serializer = new System.Runtime.Serialization.Formatters.Binary.BinaryFormatter();
                serializer.Serialize(fileStream, gameState);
            }
        }
        catch (Exception ex)
        {
            Console.WriteLine($"Error saving game state: {ex.Message}");
        }
    }
    static GameState LoadGameState()
    {
        try
        {
            if (File.Exists("savegame.dat"))
            {
                using (FileStream fileStream = new FileStream("savegame.dat", FileMode.Open))
                {
                    var serializer = new System.Runtime.Serialization.Formatters.Binary.BinaryFormatter();
                    return (GameState)serializer.Deserialize(fileStream);
                }
            }
        }
        catch (Exception ex)
        {
            Console.WriteLine($"Error loading game state: {ex.Message}");
        }

        return null;
    }
