    static void SaveToFile(string filePath, Action method)
    {
        try
        {
            // Redirect Console.Out to a new StringWriter
            using (StringWriter stringWriter = new StringWriter())
            {
                Console.SetOut(stringWriter);

                // Invoke the method to get its output
                method.Invoke();

                // Get the output from StringWriter
                string methodOutput = stringWriter.ToString();

                // Using File.WriteAllText to write the content to the file
                File.WriteAllText(filePath, methodOutput);
            }
        }
        catch (Exception ex)
        {
            Console.WriteLine("Error while saving to file: " + ex.Message);
        }
    }

    private static string CaptureConsoleOutput(Action action)
    {
        using (StringWriter stringWriter = new StringWriter())
        {
            try
            {
                Console.SetOut(stringWriter);
                action.Invoke();
                return stringWriter.ToString();
            }
            finally
            {
                // Ensure that Console.Out is restored even if an exception occurs
                Console.SetOut(Console.Out);
            }
        }
    }
