    private class DualTextWriter : TextWriter
    {
        private readonly TextWriter originalWriter;
        private readonly TextWriter additionalWriter;

        public DualTextWriter(TextWriter original, TextWriter additional)
        {
            originalWriter = original ?? throw new ArgumentNullException(nameof(original));
            additionalWriter = additional ?? throw new ArgumentNullException(nameof(additional));
        }

        public override Encoding Encoding => originalWriter.Encoding;

        public override void Write(char value)
        {
            originalWriter.Write(value);
            additionalWriter.Write(value);
        }

        public override void Write(string value)
        {
            originalWriter.Write(value);
            additionalWriter.Write(value);
        }

        public override void WriteLine(string value)
        {
            originalWriter.WriteLine(value);
            additionalWriter.WriteLine(value);
        }
    }

